import React , {useState , useEffect} from 'react';
import {Link} from 'react-router-dom'
import './Navbar.css';
import Button from './Button'
function Navbar() {
    const [click,setClick]=useState(false);
    const [button,setButton]=useState(true);

    const handleClick=()=>{
        setClick(!click)
    }

    const closeMobMenu = ()=>{
        setClick(false);
    }

    const showButton = ()=>{
        if(window.innerWidth<=960){
            setButton(!button)
        }else{
            setButton(button)
        }
    }

    useEffect(()=>{
        showButton()
    },[])
    window.addEventListener('resize',showButton);

    return (
        <>
        <nav className="navbar">
            <div className="navbar-container">
                <Link to='/' className='navbar-logo' onClick={closeMobMenu}>
                SAFARI <i class="fas fa-spa"></i>
                </Link>
            <div className='menu-icon' onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ?'nav-menu active' :'nav-menu'}>
                    <li className='nav-item'>
                        <Link to='/' className='nav-links' onClick={closeMobMenu}>Home</Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/services' className='nav-links' onClick={closeMobMenu}>Services</Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/products' className='nav-links' onClick={closeMobMenu}>About Us</Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-links-mobile' onClick={closeMobMenu}>Sign Up</Link>
                    </li>
                </ul>
                {button && <Button buttonStyle='btn--outline'>Sign Up</Button>}
            </div>
        </nav>
        </>
    )
}

export default Navbar
